import React from 'react';
import logo from './logo.svg';
import './App.css';

const Task = (value) => {
  return (
    <li key={value}>{value}</li> // a key serve para identificar itens dentro de uma lista. No caso, eu coloco com o mesmo do valor que vou colocar para ser mais facil a localização
  );
}
const tarefas = ["acordar", "tomar banho", "malhar", "estudar", "comer", "dormir"];
class App extends React.Component {
  render() {
    return (
      <ul>{tarefas.map((tarefa) => Task(tarefa))}</ul>
    )
  }
}

export default App;
