import React from 'react'
import './App.css';
import PersonalData from './PersonalData'
import LastJob from './LastJob'
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      cpf: 0,
      adress: '',
      city: '',
      brazilState: '',
      house: '',
      curriculum: '',
      job: 'yes',
      jobDescription: '',
    }
  }
  changeHandler = event => {
    let { name, value } = event.target;

    if (name === 'name') value = value.toUpperCase();
    this.updateState(name, value);
  }

  removeSpecial = (e) => {
    const { value } = e.target
    this.setState({ adress: value.replace(/[^a-zA-Zs]/g, "") })
  }
  onBlurCheck = (e) => {
    let { name } = e.target;
    console.log(e.target.value)
    if (name === 'city') {
      e.target.value = e.target.value.match(/^\d/) ? '' : e.target.value;
    }
  }
  updateState = (e) => {
    this.setState
      ({ brazilState: e.target.value })
  }

  alertJob = () => {
    if (this.state.job === 'yes') {
      alert('Preencha com cuidado esta informação.');
      this.setState({ job: 'no' })
    }
  }

  render() {

    return (
      <div>
        <PersonalData upperCase={this.changeHandler} stateName={this.state.name}
          removeSpecial={this.removeSpecial} stateAdress={this.state.adress}
          onMouseEnter={this.onBlurCheck} cityState={this.state.city}
          updateState={this.updateState} brazilState={this.state.brazilState}

        />
        <LastJob alertJob={this.alertJob}/>
      </div>
    )
  }
}

export default App;
