import React from 'react'
import './App.css';
import PersonalData from './PersonalData'

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      cpf: 0,
      adress: '',
      city: '',
      brazilState: '',
      house: false,
      apt: false,
      curriculum: '',
      job: 'yes',
      jobDescription: '',
    }
  }
  upperCase = (e) => {
    const { value } = e.target
    this.setState({ name: value.toUpperCase() })
  }
  removeSpecial = (e) => {
    const { value } = e.target
    this.setState({ adress: value.replace(/[^a-zA-Zs]/g, "") })
  }
  onBlurCheck = (e) => {
    let { name } = e.target;
    console.log(e.target.value)
    if (name === 'city') {
      e.target.value = e.target.value.match(/^\d/) ? '' : e.target.value;

    }
  }
  updateState = (e) => {
    this.setState
      ({ brazilState: e.target.value })
  }

  alertJob = () => {
    if (this.state.job === 'yes') {
      alert('Preencha com cuidado esta informação.');
      this.setState({ job: 'no' })
    }
  }

  render() {
    const brazilStates = [
      "Acre",
      "Alagoas",
      "Amapá",
      "Amazonas",
      "Bahia",
      "Ceará",
      "Distrito Federal",
      "Espirito Santo",
      "Goiás",
      "Maranhão",
      "Mato Grosso do Sul",
      "Mato Grosso",
      "Minas Gerais",
      "Pará",
      "Paraíba",
      "Paraná",
      "Pernambuco",
      "Piauí",
      "Rio de Janeiro",
      "Rio Grande do Norte",
      "Rio Grande do Sul",
      "Rondônia",
      "Roraima",
      "Santa Catarina",
      "São Paulo",
      "Sergipe",
      "Tocantins",
    ];
    return (
      <div>
        <PersonalData upperCase={this.upperCase} stateName={this.state.name}
         removeSpecial={this.removeSpecial} stateAdress={this.state.adress}
         onMouseEnter={this.onBlurCheck} cityState={this.state.city}
         updateState={this.updateState} brazilState={this.state.brazilState}
         />
        <fieldset>
          <legend>Dados do seu último emprego</legend>
          <form>
            <label htmlFor='curriculum'>
              Resumo do seu currículo:
              <textarea name='curriculum' maxLength='1000' required>Escreva aqui</textarea>
            </label>
            <label htmlFor='job'>
              Cargo:
              <input type='text' name='job' maxLength='40' required onMouseEnter={this.alertJob}></input>
            </label>
            <label type='jobDescription'>
              Descrição do cargo:
              <input type='text' name='jobDescription' maxLength='500' required></input>

            </label>
          </form>
        </fieldset>
      </div>
    )
  }
}

export default App;
